changeSet.sql:
DELETE FROM report_file where section_id in (select id from section where report_type in ('R0031'));
DELETE FROM publication_schedule where section_id in (select id from section where report_type in ('R0031'));
UPDATE section SET display_order = display_order - 1 WHERE display_order >= (select display_order from section where report_type in ('R0031')) AND section_type = 'ETS';
DELETE FROM section where report_type in ('R0031');
DELETE FROM migrator_history WHERE migrator_name = 'PUBLICATION_SITE_MIGRATOR';
